import CryptoJS from 'crypto-js';

const key = CryptoJS.enc.Utf8.parse('my-secret-key'); // replace with your own secret key
const iv = CryptoJS.lib.WordArray.random(16);

export function encryptId(id) {
  const encrypted = CryptoJS.AES.encrypt(id, key, { iv: iv });
  return iv.concat(encrypted.ciphertext).toString(CryptoJS.enc.Base64);
}

export function decryptId(ciphertext) {
  const ciphertextBytes = CryptoJS.enc.Base64.parse(ciphertext);
  const iv = ciphertextBytes.slice(0, 16);
  const encrypted = ciphertextBytes.slice(16);

  try {
    const decrypted = CryptoJS.AES.decrypt({ ciphertext: encrypted }, key, { iv: iv });
    return decrypted.toString(CryptoJS.enc.Utf8);
  } catch (error) {
    console.error('Error decrypting ciphertext:', error);
    return null; // or throw an error
  }
}
