import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { saltValue } from '../salting.js';
import { encryptId, decryptId }  from '../salting.js';
import CryptoJS from 'crypto-js';
import './css/Dashboard.css'

function CarDashboard() {

  const [cars, setCars] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get(`http://localhost:8000/api/cars`);
      setCars(response.data);
    }
    fetchData();
  }, []);

  const handleDelete = (id) => {
    const confirmed = window.confirm("Are you sure you want to delete this contact?");

    if (confirmed) {
      axios.delete(`http://localhost:8000/api/cars/${id}`)
        .then((res) => {
          console.log(res.data);
          alert(res.data.message);
          setCars((prevCars) => prevCars.filter((car) => car.id !== id));
        })
        .catch((err) => {
          console.error(err);
          alert('Error deleting data');
        });
    }
  };
  const handleEdit = (id) => {
    const saltedId = encryptId(id);
    window.localStorage.setItem('editId', saltedId); // Store the encrypted ID in localStorage
    window.location.href = `/add/${saltedId}?isEdit=true`;
  };

  // In the other page
  const editId = window.localStorage.getItem('editId');
  const decryptedId = decryptId(editId);
  if (decryptedId) {
    alert(decryptedId)
  }



  return (
    <div>
    <div className='container-dash '>
      <h1>Car Dashboard</h1>
      <table className="car-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Brand</th>
            <th>Model</th>
            <th>Year</th>
            <th>Price</th>
            <th>Gearbox</th>
            <th>Fuel</th>
            <th>Image</th>
            <th>Available</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cars.map(car => (
            <tr key={car.id}>
              <td>{car.id}</td>
              <td>{car.brand}</td>
              <td>{car.model}</td>
              <td>{car.year}</td>
              <td>{car.Price}</td>
              <td>{car.gearbox}</td>
              <td>{car.fuel}</td>
              <td><img  src={`http://localhost:8000/storage/${car.image}`} alt="car image" /></td>
              <td>{car.available ? 'Yes' : 'No'}</td>
              <td>
                <button className="edit-button" onClick={() => handleEdit(car.id) }>Edit</button>
                <button className="delete-button" onClick={() =>  handleDelete(car.id)}>Delete</button>
              </td>
            </tr>

          ))}
        </tbody>
      </table>
    </div>




    </div>
  );
}

export default CarDashboard;

