import { randomBytes, createHash } from 'crypto';

function saltAndHashId(id) {
  const salt = randomBytes(16).toString('hex');
  const hash = createHash('sha256').update(id + salt).digest('hex');
  return salt + hash;
}

function desaltId(saltedId) {
  const salt = saltedId.substr(0, 32);
  const hash = saltedId.substr(32);
  return { salt, hash };
}

export default { saltAndHashId, desaltId };
