import { useState } from "react";
import { Form, Button } from "react-bootstrap";
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
// import 'bootstrap/dist/css/bootstrap.min.css'
import { API_URL } from '../utils/value.js'


function CarForm() {
  const [brand, setBrand] = useState("");
  const [model, setModel] = useState("");
  const [year, setYear] = useState("");
  const [price, setPrice] = useState("");
  const [gearbox, setGearbox] = useState("");
  const [fuel, setFuel] = useState("");
  const [image, setImage] = useState(null);
  const [available, setAvailable] = useState(true);

//   const handleSubmit = async (event) => {
//     event.preventDefault();

//     const formData = new FormData();
//     formData.append('brand', brand);
//     formData.append('model', model);
//     formData.append('year', year);
//     formData.append('price', price);
//     formData.append('gearbox', gearbox);
//     formData.append('fuel', fuel);
//     formData.append('image', image);
//     formData.append('available', available);

//     try {
//       const response = await axios.post(`${API_URL}/cars/add`, formData);
//       console.log(response.data);
//     } catch (error) {
//       console.log(error.response.data);
//     }

//   };

// import React, { useState, useEffect } from 'react';
// import axios from 'axios';
// import './ContactForm.css';
// import { useNavigate, useParams } from 'react-router-dom';

// const ContactForm = () => {
    const { id } = useParams();
    const isEdit = id !== undefined;

        const formData = new FormData();
    formData.append('brand', brand);
    formData.append('model', model);
    formData.append('year', year);
    formData.append('price', price);
    formData.append('gearbox', gearbox);
    formData.append('fuel', fuel);
    formData.append('image', image);
    formData.append('available', available);

    const navigate = useNavigate();

    // const [formData, setFormData] = useState({
    //     name: '',
    //     phone: '',
    //     address: '',
    //     email: '',
    // });

    useEffect(() => {
        if (isEdit) {
            // Fetch the contact data by id from the server and set the form data
            axios
                .get(`http://localhost/React3-main/Php/ReadOne.php?id=${id}`)
                .then((res) => {
                    const { name, phone, address, email } = res.data;
                    setFormData({ name, phone, address, email });
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error fetching contact data');
                    navigate('/Home');
                });
        }
    }, [isEdit, id, navigate]);

    const handleChange = (e) => {

        setFormData({ ...formData, [e.target.name]: e.target.value });

    };

    const verified = () =>{
        const rule = /^[a-zA-Z ]{2,40}$/;

        if (formData.name.length > 15) {
            alert('Enter Valid Name');

            return false;
        } else if (formData.phone.length !== 10) {
            alert('Enter Valid Phone Number');

            return false;
        } else if (formData.address.length > 30) {
            alert('Address length should be less than 30');

            return false;
        }
        if (!rule.test(formData.name)) {
            alert('Enter a valid name');
            return false;
        }
        return true;

    }

    const handleSubmit = (e) => {

        if(verified()) {
            e.preventDefault();
            const data = new URLSearchParams();
            data.append('name', formData.name);
            data.append('phone', formData.phone);
            data.append('address', formData.address);
            data.append('email', formData.email);

            const url = isEdit
                ? `http://localhost/React3-main/Php/Update.php?id=${encodeURIComponent(id)}`
                : 'http://localhost/React3-main/Php/Create.php';

            axios
                .post(url, data)
                .then((res) => {
                    console.log("data is coming", res.data);
                    alert("Data Inserted Successfully");
                    setFormData({name: '', phone: '', address: '', email: ''});
                    navigate('/Home');
                })
                .catch((err) => {
                    console.error(err);
                    // alert for error
                    alert('Error storing data');
                });
        }

    };

  return (
    <Form onSubmit={handleSubmit} className="mx-auto mt-5 p-4 rounded">
      <h4 className="text-center mb-4">Add Car</h4>
      <Form.Group controlId="formBrand">
        <Form.Label className="form-label">Brand</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter brand"
          value={brand}
          onChange={(event) => setBrand(event.target.value)}
          className="form-control"
        />
      </Form.Group>
      <Form.Group controlId="formModel">
        <Form.Label className="form-label">Model</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter model"
          value={model}
          onChange={(event) => setModel(event.target.value)}
          className="form-control"
        />
      </Form.Group>
      <Form.Group controlId="formYear">
        <Form.Label className="form-label">Year</Form.Label>
        <Form.Control
          type="number"
          placeholder="Enter year"
          value={year}
          onChange={(event) => setYear(event.target.value)}
          className="form-control"
        />
      </Form.Group>
      <Form.Group controlId="formPrice">
        <Form.Label className="form-label">Price</Form.Label>
        <Form.Control
          type="number"
          placeholder="Enter price"
          value={price}
          onChange={(event) => setPrice(event.target.value)}
          className="form-control"
        />
      </Form.Group>
      <Form.Group controlId="formGearbox">
        <Form.Label className="form-label">Gearbox</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter gearbox"
          value={gearbox}
          onChange={(event) => setGearbox(event.target.value)}
          className="form-control"
        />
      </Form.Group>
      <Form.Group controlId="formFuel">
        <Form.Label className="form-label">Fuel</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter fuel"
          value={fuel}
          onChange={(event) => setFuel(event.target.value)}
          className="form-control"
        />
      </Form.Group>
      <Form.Group controlId="formImage">
        <Form.Label className="form-label">Image</Form.Label>
        <Form.Control
          type="file"
          onChange={(event) => setImage(event.target.files[0])}
          className="form-control"
        />
      </Form.Group>
      <Form.Group controlId="formAvailable">
        <Form.Check
          type="checkbox"
          label="Available"
          checked={available}
          onChange={(event) => setAvailable(event.target.checked)}
        />
      </Form.Group>
      <Button variant="primary" type="submit" className="btn-primary mt-3">
        Submit
      </Button>
    </Form>
  );
}

export default CarForm;
