// import CryptoJS from 'crypto-js'
import CryptoJS from 'crypto-js';

// Generate a random salt using CryptoJS
const crypto = require('crypto');

function saltAndHashId(id) {
  const salt = crypto.randomBytes(16).toString('hex');
  const hash = crypto.createHash('sha256').update(id + salt).digest('hex');
  return salt + hash;
}

function desaltId(saltedId) {
  const salt = saltedId.substr(0, 32);
  const hash = saltedId.substr(32);
  return { salt, hash };
}

// Example usage
const id = '12345';
const saltedId = saltAndHashId(id);

console.log('Salted ID:', saltedId);

const { salt, hash } = desaltId(saltedId);

console.log('Salt:', salt);
console.log('Hash:', hash);
