import CryptoJS from 'crypto-js';

export function saltAndHashId(id) {
  const salt = CryptoJS.lib.WordArray.random(16).toString();
  const hash = CryptoJS.SHA256(id + salt).toString();
  return salt + hash;
}

export function desaltId(saltedId) {
  const salt = saltedId.substr(0, 32);
  const hash = saltedId.substr(32);
  const id = hash.slice(salt.length);
  return id;
}
