import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CarCard({ cars }) {


  const [car, setCars] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get(`http://localhost:8000/api/cars`);
      setCars(response.data);
    }
    fetchData();
  }, []);


  return (
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={`http://localhost:8000/storage/${car.image}`} alt="car image" /> 
      <Card.Body>
        <Card.Title>{car.brand} {car.model}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">{car.year}</Card.Subtitle>
        <Card.Text>
          Gearbox: {car.gearbox}<br />
          Fuel: {car.fuel}<br />
          Price: {car.price}<br />
        </Card.Text>
        <Button variant="primary" disabled={!car.available}>Rent Now</Button>
      </Card.Body>
    </Card>
  );
}

export default CarCard;
