import { lib, enc } from 'crypto-js';

export function saltAndHashId(id) {
  const salt = lib.WordArray.random(16);
  const hash = enc.Hex.stringify(lib.WordArray.create(id + salt).sha256());
  return salt.toString() + hash;
}

export function desaltId(saltedId) {
  const salt = saltedId.substr(0, 32);
  const hash = saltedId.substr(32);
  return { salt, hash };
}
