import CryptoJS from 'crypto-js';

// Define the salt value as a constant
const salt = CryptoJS.lib.WordArray.random(32).toString();


/**
 * Salts a value by concatenating it with the salt value
 * and then hashing it with SHA256.
 *
 * @param {string} value - The value to salt.
 * @returns {string} The salted and hashed value.
 */
export function saltValue(value) {
  const valueToHash = salt + value;
  return CryptoJS.SHA256(valueToHash).toString();
}

/**
 * Desalts a value by hashing the salt and the original value,
 * and then comparing the result with the hashed value.
 * If they match, returns the original value.
 *
 * @param {string} hashedValue - The hashed value to desalt.
 * @returns {string|null} The original value, or null if the hashed value is incorrect.
 */
export function desaltValue(hashedValue) {
    if (typeof hashedValue !== 'string' || hashedValue.length < salt.length) {
      return null;
    }

    const valueToCheck = hashedValue.substring(salt.length);
    const hashedValueToCheck = CryptoJS.SHA256(salt + valueToCheck).toString();

    if (hashedValueToCheck === hashedValue) {
      return valueToCheck;
    } else {
      return null;
    }
  }
