import CryptoJS from 'crypto-js'
import { randomBytes, createHash } from 'crypto-js';

const salt = CryptoJS.lib.WordArray.random(32).toString()




export function saltAndHashId(id) {
  const salt = randomBytes(16).toString('hex');
  const hash = createHash('sha256').update(id + salt).digest('hex');
  return salt + hash;
}

export function desaltId(saltedId) {
  const salt = saltedId.substr(0, 32);
  const hash = saltedId.substr(32);
  return { salt, hash };
}
