// import CryptoJS from 'crypto-js';

export function desaltId(saltedId) {
  const salt = saltedId.substr(0, 32);
  const hash = saltedId.substr(32);
  const id = CryptoJS.SHA256(hash + salt).toString();
  return id;
}
