import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar.js';
import RentForm from '../components/form.js';
import './css/cars.css';

function RentList() {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);
  const [showRentForm, setShowRentForm] = useState(false);
  const [userId, setUserId] = useState(1);

  useEffect(() => {
    axios.get('http://localhost:8000/api/rents')
      .then(response => {
        setCars(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const rentCar = (car) => {
    setSelectedCar(car);
    setShowRentForm(true);
  }

  const submitRentForm = (carId, userId) => {
    // Call the API to rent the selected car
    axios.post('http://localhost:8000/api/rents', { car_id: carId, user_id: userId })
      .then(response => {
        console.log(response.data);
        setShowRentForm(false);
      })
      .catch(error => {
        console.log(error);
      });
  }

  const closeRentForm = () => {
    setSelectedCar(null);
    setShowRentForm(false);
  }

  return (
    <div className='main-cars'>
      <Navbar />
      {showRentForm && selectedCar && (
        <RentForm
          car={selectedCar}
          onSubmit={(carId) => submitRentForm(carId, userId)}
          onClose={() => closeRentForm()}
        />
      )}
      <div className="product-list">
        {cars.map((car) => (
          <div className="product" key={car.id}>
            <h3>{car.brand} {car.model}</h3>
            <img id="img" src={`http://localhost:8000/storage/${car.image}`} alt={car.name} />
            <p>{car.fuel}</p>
            <p>{car.Price} Rs</p>
            <button onClick={() => rentCar(car)}>Rent Car</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default RentList;
